package com.tdm.domain.model.problem;

import java.io.Serializable;

/**
 * Uniquely identifies a particular cargo. Automatically generated by the
 * application.
 */
@SuppressWarnings("serial")
public class ProblemId implements Serializable {

    private String id;

    public ProblemId() {
    }

    public ProblemId(String id) {
        this.id = id;
    }

    public String getIdString() {
        return id;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) {
            return true;
        }
        if (o == null || getClass() != o.getClass()) {
            return false;
        }

        ProblemId other = (ProblemId) o;

        return sameValueAs(other);
    }

    @Override
    public int hashCode() {
        return id.hashCode();
    }

    boolean sameValueAs(ProblemId other) {
        return other != null && this.id.equals(other.id);
    }

    @Override
    public String toString() {
        return id;
    }
}
